async filetype => {
  const imageFiles = FilePicker1.files;
  if (imageFiles.length > 0) {
    const imageUrls = await Promise.all(imageFiles.map(item => {
      const filename = numberRules.randomFilename() + '_' + item.name;
      return Uploadimages.run({
        'filecontent': item,
        'filename': filename
      });
    }));
    const simpImageUrls = imageUrls.map(item => item.signedUrl.split('?')[0]);
    const imageUrlsData = simpImageUrls.map(item => {
      return '("' + appsmith.store.serialNumber + '","' + filetype + '","' + item + '","' + appsmith.store.qwuser.userid + '","' + Date() + '")';
    }).join();
    await InsertImagesUrl.run({
      'data': imageUrlsData
    });
  }
}

