actionConfiguration.body:
async (filetype, imageFiles, filePickerWidget) => {
  if (imageFiles.length > 0) {
    const imageUrls = await Promise.all(imageFiles.map(item => {
      const filename = numberRules.randomFilename() + '_' + item.name;
      return Uploadimages.run({
        'filecontent': item,
        'filename': filename
      });
    }));
    const simpImageUrls = imageUrls.map(item => item.signedUrl.split('?')[0]);
    const imageUrlsData = simpImageUrls.map(item => {
      return '("' + ticketDetails.data.ticketNumber + '","' + filetype + '","' + item + '","' + appsmith.store.qwuser.userid + '","' + Date() + '")';
    }).join();
    await InsertImagesUrl.run({
      'data': imageUrlsData
    });
    showAlert('图片上传成功', 'success');
    SelectImages.run();
    resetWidget(filePickerWidget, true);
  }
}

