            { x: svgWidth / 3, y: (2 * svgHeight) / 4 },
            { x: (2 * svgWidth) / 3, y: (2 * svgHeight) / 4 },
            { x: svgWidth / 3, y: (3 * svgHeight) / 4 },
            { x: (2 * svgWidth) / 3, y: (3 * svgHeight) / 4 },
        ],
    };

